import React from 'react';
import axios from 'axios';
import toast from 'react-hot-toast';
import { Link } from 'react-router-dom';

export default function Ad({ ad }) {

  const handleDelete = async (adId, e) => {
    e.preventDefault();

    const isConfirmed = window.confirm('Oprvadu chcete tento inzerát smazat?');
    if (!isConfirmed) {
      return;
    }

    try {
      const response = await axios.delete(`http://localhost:3001/ad/${adId}`, { withCredentials: true });
      if (response.data.Status === 'Success') {
        toast.success('Inzerát úspěšně smazán.');
      } else {
        toast.error(response.data.Error);
      }
    } catch (error) {
      toast.error('Chyba při mazání inzerátu.');
    }
  };

  return (
    <Link to={`/car/${ad.id}`} style={{ textDecoration: 'none', color: 'inherit'}}>
      <div className="card mb-3" style={{ display: 'flex', flexDirection: 'column', height: '100%', padding: '15px' }}>
        <div className="card-body" style={{ display: 'flex', flexDirection: 'column', flexGrow: 1, justifyContent: 'space-between' }}>
          <h5 className="card-title fw-bold">{ad.nazev}</h5>
          <img alt='auto' className='img-fluid' src={ad.obrazek}></img>
          <h5 className="card-title fw-bold" style={{ color: data.car.stav === 'Aktivní' ? 'green' : data.car.stav === 'Rezervovaný' ? 'orange' : data.car.stav === 'Zrušený' ? 'red' : 'black' }}>{data.car.stav}</h5>
          <p className="card-text">Inzerent: {ad.jmeno} {ad.prijmeni}</p>
          <p className="card-text">Email: {ad.email}</p>
          <div className="d-flex justify-content-between">
            <button style={{ width: '100%' }} className="btn btn-danger" onClick={(e) => handleDelete(ad.id, e)}>
              Smazat
            </button>
          </div>
        </div>
      </div>
    </Link>
  );
}
