  useEffect(() => {
    const fetchCarData = async () => {
      try {
        const response = await axios.post('http://locahost:3001/car/${car.id}', {
          carId: id,  // Poslání ID auta v těle požadavku
        });
        setCarData(response.data);  // Nastavení dat auta
        setLoading(false);  // Nastavení načítání na false
      } catch (err) {
