import React from 'react'
import CheckAuth from '../functions/checkAuthAdmin';
import { useNavigate } from 'react-router-dom';
import { useEffect, useState } from 'react';
import User from '../components/Users.js';
import toast from 'react-hot-toast';
import axios from 'axios';


export default function ManageUsers() {
    const navigate = useNavigate();
    const [loading, setLoading] = useState(true);
    const [users, setUsers] = useState([]);

    useEffect(() => {
        CheckAuth(navigate).then(() => {
            setLoading(false);
        });
    }, []);

    useEffect(() => {
        axios.get(`http://localhost:3001/userList`, { withCredentials: true })
            .then(res => {
                if (res.data.Status === "Success") {
                    setUsers(res.data.users)
                } else {
                    toast.error(res.data.Error)
                }
            })
            .catch(error => {
                toast.error("Došlo k chybě při náčítání uživatelů. " + error);
            });
    }, [])

    if (loading) {
        return <></>;
    }
    return (
        <div className='container text-center'>
            <h2>Správa Uživatelů</h2>
            <hr></hr>
            <div className="row">
                <div className="col col-lg-12 col-xl-12">
                    {users.map(user => (
                        <User key={user.id} userList={user} />
                    ))}
                </div>
            </div>
        </div>
    )
}
