import React from 'react'
import axios from 'axios';
import { useParams } from 'react-router-dom';
import { useEffect } from 'react';
import { useState } from 'react';
import toast from 'react-hot-toast';
import { useNavigate } from 'react-router-dom';

export default function SingleCar() {
  const { id } = useParams();
  const [carData, setCarData] = useState(null);

  const navigate = useNavigate();
  const handleGoBack = () => {
    navigate(-1);
  };

  useEffect(() => {
    axios.get(`http://localhost:3001/car/${id}`)
      .then(res => {
        if (res.data.Status === "Success") {
          setCarData(res.data.result[0]);
        } else {
          toast.error(res.data.Error)
        }
      })
      .catch(error => {
        toast.error("Došlo k chybě při náčítání stránky profilu. " + error);
      });
  }, [id]);

  const renderSpecification = (name, data, color) => (
    <div className='col col-xxl-11 col-xl-11 col-lg-11 col-md-11 col-sm-11 d-flex justify-content-between align-items-center' style={{ backgroundColor: {color}, height: '40px' }}>
      <span className='fw-bold'>{name}</span>
      <span className='fw-bold'>{data}</span>
    </div>
  );

  if (!carData) {
    return <div>Načítání...</div>;
  }
  return (
    <>
      <div className='container'>
        <div className='row'>
          <div className='col col-xxl-12 col-xl-12 col-lg-12 col-md-12 col-sm-12'>
            <h6 onClick={handleGoBack} style={{ cursor: 'pointer' }}>&lt; Zpět na výpis</h6>
          </div>
        </div>
        <div className='row'>
          <div className='col col-xxl-12 col-xl-12 col-lg-12 col-md-12 col-sm-12 d-flex justify-content-between align-items-center'>
            <h2>{carData.inzerat_nazev}<span style={{ color: carData.stav === 'Aktivní' ? 'green' : carData.stav === 'Rezervovaný' ? 'orange' : carData.stav === 'Zrušený' ? 'red' : 'black' }}> - {carData.stav}</span></h2>
            <h2>{Number(carData.cena).toLocaleString('cs-CZ')} Kč</h2>
          </div>
        </div>
        <div className='row' style={{ marginBottom: '30px' }}>
          <div className='col col-xxl-12 col-xl-12 col-lg-12 col-md-12 col-sm-12'>
            <img src='https://www.aaaauto.cz/data/photo/thumb/13585_430x0x75.jpg'></img>
          </div>
        </div>
        <div className='row'>
          <div className='col col-xxl-12 col-xl-12 col-lg-12 col-md-12 col-sm-12 d-flex flex-column justify-content-center align-items-center'>
            <h2>Specifikace Vozu</h2>
            <hr style={{ marginBottom: '30px', width: '100%' }}></hr>
          </div>
        </div>
        <div className='row d-flex justify-content-center align-items-center'>
          <div className='col col-xxl-6 col-xl-6 col-lg-6 col-md-6 col-sm-6'>
            <div className='row d-flex justify-content-center align-items-center'>
              {renderSpecification('Značka', carData.znacka, 'lightgray')}
              <div className='col col-xxl-11 col-xl-11 col-lg-11 col-md-11 col-sm-11 d-flex justify-content-between align-items-center' style={{ backgroundColor: 'white', height: '40px' }}>
                <span className='fw-bold'>Model</span>
                <span className='fw-bold'>{carData.model_nazev}</span>
              </div>
              <div className='col col-xxl-11 col-xl-11 col-lg-11 col-md-11 col-sm-11 d-flex justify-content-between align-items-center' style={{ backgroundColor: 'lightgray', height: '40px' }}>
                <span className='fw-bold'>Karoserie</span>
                <span className='fw-bold'>{carData.karoserie}</span>
              </div>
              <div className='col col-xxl-11 col-xl-11 col-lg-11 col-md-11 col-sm-11 d-flex justify-content-between align-items-center' style={{ backgroundColor: 'white', height: '40px' }}>
                <span className='fw-bold'>Palivo</span>
                <span className='fw-bold'>{carData.palivo}</span>
              </div>
              <div className='col col-xxl-11 col-xl-11 col-lg-11 col-md-11 col-sm-11 d-flex justify-content-between align-items-center' style={{ backgroundColor: 'lightgray', height: '40px' }}>
                <span className='fw-bold'>Pohon Kol</span>
                <span className='fw-bold'>{carData.pohon}</span>
              </div>
              <div className='col col-xxl-11 col-xl-11 col-lg-11 col-md-11 col-sm-11 d-flex justify-content-between align-items-center' style={{ backgroundColor: 'white', height: '40px' }}>
                <span className='fw-bold'>Převodovka</span>
                <span className='fw-bold'>{carData.prevodovka}</span>
              </div>
              <div className='col col-xxl-11 col-xl-11 col-lg-11 col-md-11 col-sm-11 d-flex justify-content-between align-items-center' style={{ backgroundColor: 'lightgray', height: '40px' }}>
                <span className='fw-bold'>Počet Dveří</span>
                <span className='fw-bold'>{carData.pocet_dveri}</span>
              </div>
            </div>
          </div>
          <div className='col col-xxl-6 col-xl-6 col-lg-6 col-md-6 col-sm-6'>
            <div className='row d-flex justify-content-center align-items-center'>
              <div className='col col-xxl-11 col-xl-11 col-lg-11 col-md-11 col-sm-11 d-flex justify-content-between align-items-center' style={{ backgroundColor: 'lightgray', height: '40px' }}>
                <span className='fw-bold'>Barva</span>
                <span className='fw-bold'>{carData.barva}</span>
              </div>
            </div>
            <div className='row d-flex justify-content-center align-items-center'>
              <div className='col col-xxl-11 col-xl-11 col-lg-11 col-md-11 col-sm-11 d-flex justify-content-between align-items-center' style={{ backgroundColor: 'white', height: '40px' }}>
                <span className='fw-bold'></span>
                <span className='fw-bold'>{carData.barva}</span>
              </div>
            </div>
          </div>
        </div>
      </div>
    </>
  )
}
