import React from 'react';
import axios from 'axios';
import toast from 'react-hot-toast';

const User = ({ user }) => {
    const handleDelete = async () => {
        try {
            const response = await axios.delete(`http://localhost:3001/user/${user.id}`, { withCredentials: true });
            if (response.data.Status === 'Success') {
                toast.success('Uživatel úspěšně smazán.');
                window.location.reload();
            } else {
                toast.error(response.data.Error);
            }
        } catch (error) {
            toast.error('Chyba při mazání uživatele.');
        }
    };

    const handleChangeRole = async () => {
        try {
            const newRole = user.role === 'admin' ? 'user' : 'admin';
            const response = await axios.put(
                `http://localhost:3001/user/${user.id}/role`,
                { role: newRole },
                { withCredentials: true }
            );
            if (response.data.Status === 'Success') {
                toast.success(`Role změněna na ${newRole}.`);
                window.location.reload();
            } else {
                toast.error(response.data.Error);
            }
        } catch (error) {
            toast.error('Chyba při změně role.');
        }
    };

    return (
        <div className="card mb-3 col-12 col-xxl-4 col-xl-4 col-lg-4 col-md-6 col-sm-6">
            <div className="card-body">
                <h5 className="card-title">{`${user.jmeno} ${user.prijmeni}`}</h5>
                <p className="card-text">Email: {user.email}</p>
                <p className="card-text">Telefon: {user.telefon}</p>
                <p className="card-text">Role: {user.role}</p>
                <p className="card-text">Datum registrace: {new Date(user.datum_registrace).toLocaleDateString()}</p>
                <div className="d-flex justify-content-between">
                    <button className="btn btn-danger" onClick={handleDelete}>
                        Smazat
                    </button>
                    <button className="btn btn-primary" onClick={handleChangeRole}>
                        Změnit roli
                    </button>
                </div>
            </div>
        </div>
    );
};

export default User;
