import React from 'react'
import Navbar from '../components/Navbar';
import Footer from '../components/Footer';

export default function Profile() {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    // Zkontrolujeme, zda existuje platný token v cookie
    const token = document.cookie.split(';').find(item => item.trim().startsWith('authToken='));
    
    if (!token) {
      // Pokud není token, přesměrujeme uživatele na stránku přihlášení
      navigate('/prihlaseni');
    } else {
      // Pokud je token, ověříme jeho platnost (např. pomocí volání na server)
      // Tento krok závisí na tvé implementaci, například můžeš poslat požadavek na server pro ověření tokenu.
      // Pokud je platný, nastavíme isAuthenticated na true.
      setIsAuthenticated(true);
    }
  }, [navigate]);

  if (!isAuthenticated) {
    return <div>Loading...</div>;  // Můžeš přidat nějaký loading indikátor během ověřování
  }
  return (
    <>

    </>
  )
}
