import React from 'react';
import { Link, useNavigate } from "react-router-dom";
import { useState } from "react";
import axios from 'axios';
import { toast } from 'react-hot-toast';
import { useEffect } from 'react';

const CarAddFav = ({ car, isFavourite: stateFavourite}) => {
    const [auth, jePrihlasen] = useState(false);
    const [isFavourite, setIsFavourite] = useState(stateFavourite);
    const [editVisible, setEditVisible] = useState(false);
    const navigate = useNavigate();

    const toggleEditForm = (e) => {
        e.preventDefault();
        e.stopPropagation();
        if (!auth) {
            toast.error('Musíte se nejdříve přihlásit.');
            return;
        }
        setEditVisible(!editVisible);
    };

    const handleSaveChanges = (e) => {
        e.preventDefault();
        // Implementujte logiku pro uložení změn
        toast.success('Změny byly uloženy.');
        setEditVisible(false); // Skryje formulář po uložení
    };


    const deleteAd = async (e, ad, car) => {
        e.preventDefault();
        e.stopPropagation();

        if (window.confirm('Opravdu chcete smazat tento inzerát?')) {
            try {
                await axios.delete(`http://localhost:3001/ad/${ad}/${car}`, { withCredentials: true })
                    .then(res => {
                        if (res.data.Status === 'Success') {
                            toast.success('Inzerát byl úspěšně smazán.');
                            const adElement = document.getElementById(`${ad}`);
                            if (adElement) {
                                adElement.remove();
                            }
                        } else {
                            toast.error(res.data.Error)
                        }
                    });

            } catch (error) {
                toast.error(error)
            }
        }
    };

    const editAd = async (e, ad, userId) => {
        e.preventDefault();
        e.stopPropagation();

        if (!auth) {
            toast.error('Musíte se nejdříve přihlásit.');
            return;
        }

        if (car.userId !== userId) {
            toast.error('Nemáte oprávnění upravit tento inzerát.');
            return;
        }
        navigate(`/editAdd/${ad}/${car.carId}`, { withCredentials: true });
    };


    useEffect(() => {
        console.log(car.obrazky);
        axios.get('http://localhost:3001/profile', { withCredentials: true })
            .then(res => {
                if (res.data.Status === "Success") {
                    jePrihlasen(true);
                } else {
                    jePrihlasen(false);
                }
            })
    }, [])


    const handleHeartClick = async (e) => {
        e.preventDefault();
        e.stopPropagation();

        if (!auth) {
            toast.error("Musíte se nejdříve přihlásit.");
            return;
        }

        try {
            if (!isFavourite) {
                await axios.post('http://localhost:3001/favor', { carId: car.id, cena: car.cena }, { withCredentials: true });
                toast.success(`${car.nazev} byl přidán do oblíbených.`);
            } else {
                await axios.delete(`http://localhost:3001/favor/${car.id}`, { withCredentials: true });
                toast.success(`${car.nazev} byl odebrán z oblíbených.`);
            }
            setIsFavourite(!isFavourite);
        } catch (err) {
            console.error("Error updating favourites:", err);
        }
    };
    return (
        <div>
            <Link to={`/car/${car.id}`} style={{ textDecoration: 'none', color: 'inherit' }}>
                <div id={car.id} className='row mb-5 align-items-center'>
                    {/* Ostatní části komponenty */}
                    <div className="col-12 col-xl-4 col-lg-4 col-sm-12 d-flex flex-column">
                        <button 
                            style={{ marginBottom: '10px' }} 
                            onClick={toggleEditForm} 
                            className="btn-border-radius-lg form-control btn btn-outline-secondary"
                        >
                            {editVisible ? "Zrušit úpravy" : "Upravit"}
                        </button>
                        <button 
                            className="btn btn-danger" 
                            onClick={(e) => {
                                e.stopPropagation();
                                if (window.confirm('Opravdu chcete smazat tento inzerát?')) {
                                    toast.success('Inzerát byl úspěšně smazán.');
                                }
                            }}
                        >
                            Smazat
                        </button>
                    </div>
                </div>
            </Link>

            {/* Formulář pro úpravy */}
            {editVisible && (
                <div className="edit-form" style={{ marginTop: '20px', border: '1px solid #ccc', padding: '20px', borderRadius: '5px' }}>
                    <h4>Upravit inzerát</h4>
                    <form onSubmit={handleSaveChanges}>
                        <div className="form-group mb-3">
                            <label>Název</label>
                            <input type="text" className="form-control" defaultValue={car.nazev} />
                        </div>
                        <div className="form-group mb-3">
                            <label>Cena</label>
                            <input type="number" className="form-control" defaultValue={car.cena} />
                        </div>
                        <div className="form-group mb-3">
                            <label>Popis</label>
                            <textarea className="form-control" rows="3" defaultValue={car.popis}></textarea>
                        </div>
                        <button type="submit" className="btn btn-success">Uložit změny</button>
                    </form>
                </div>
            )}
        </div>
    );
};
export default CarAddFav;
