import React, { useState } from 'react';

function DragDrop() {
  const [droppedFiles, setDroppedFiles] = useState([]);

  const handleDrop = (event) => {
    event.preventDefault();
    const files = Array.from(event.dataTransfer.files);
    setDroppedFiles((prevFiles) => [...prevFiles, ...files]);
  };

  const handleDragOver = (event) => {
    event.preventDefault();
  };

  const handleRemoveFile = (event, index) => {
    event.preventDefault(); // Zamezí odeslání formuláře při kliknutí na tlačítko
    setDroppedFiles((prevFiles) => prevFiles.filter((_, i) => i !== index));
  };

  return (
    <div className="col-12 text-center">
      <div
        className="drag-drop-area"
        onDrop={handleDrop}
        onDragOver={handleDragOver}
        style={{
          border: '2px dashed #ccc',
          padding: '20px',
          borderRadius: '5px',
          cursor: 'pointer',
          backgroundColor: '#f9f9f9',
        }}
      >
        <p>Drag and drop files here, or click to select them.</p>
        <input
          type="file"
          multiple
          onChange={(e) =>
            setDroppedFiles((prevFiles) => [
              ...prevFiles,
              ...Array.from(e.target.files),
            ])
          }
          style={{
            opacity: 0,
            position: 'absolute',
            top: 0,
            left: 0,
            width: '100%',
            height: '100%',
            cursor: 'pointer',
          }}
        />
      </div>

      {droppedFiles.length > 0 && (
        <div className="file-list" style={{ marginTop: '20px' }}>
          <h4>Uploaded Files:</h4>
          <ul>
            {droppedFiles.map((file, index) => (
              <li key={index} style={{ margin: '5px 0' }}>
                {file.name}{' '}
                <button
                  onClick={(e) => handleRemoveFile(e, index)} // Přidání preventDefault
                  style={{
                    marginLeft: '10px',
                    color: 'red',
                    cursor: 'pointer',
                    border: 'none',
                    background: 'none',
                  }}
                >
                  Remove
                </button>
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
}

export default DragDrop;
