sourcesContent:
import React from 'react';
import { Link, useNavigate } from "react-router-dom";
import { useState } from "react";
import axios from 'axios';
import { toast } from 'react-hot-toast';
import { useEffect } from 'react';

const CarAddFav = ({ car, isFavourite: stateFavourite }) => {
    const [auth, jePrihlasen] = useState(false);
    const [isFavourite, setIsFavourite] = useState(stateFavourite);
    const [isEditOpen, setIsEditOpen] = useState(false);
    const [data, setData] = useState({ car });
    const [editData, setEditData] = useState({ car });
    const navigate = useNavigate();

    const [droppedFiles, setDroppedFiles] = useState([]);

    const handleDrop = (event) => {
        event.preventDefault();
        const files = Array.from(event.dataTransfer.files);

        const validFiles = files.filter(file => file.type.startsWith('image/'));
        const invalidFiles = files.filter(file => !file.type.startsWith('image/'));

        if (invalidFiles.length > 0) {
            toast.error(`Některé soubory nejsou obrázky: ${invalidFiles.map(f => f.name).join(', ')}`);
        }

        setDroppedFiles((prevFiles) => [...prevFiles, ...validFiles]);
    };

    const handleDragOver = (event) => {
        event.preventDefault();
    };

    const handleFileInputChange = (event) => {
        const files = Array.from(event.target.files);

        const validFiles = files.filter(file => file.type.startsWith('image/'));
        const invalidFiles = files.filter(file => !file.type.startsWith('image/'));

        if (invalidFiles.length > 0) {
            toast.error(`Některé soubory nejsou obrázky: ${invalidFiles.map(f => f.name).join(', ')}`);
        }

        setDroppedFiles((prevFiles) => [...prevFiles, ...validFiles]);
    };

    const handleDelete = (event, fileToDelete) => {
        event.preventDefault();
        setDroppedFiles((prevFiles) =>
            prevFiles.filter((file) => file !== fileToDelete)
        );
    };

    const handleChange = (e) => {
        console.log(editData)
        setEditData({ ...editData, [e.target.name]: e.target.value });
    };

    const deleteAd = async (e, ad, car) => {
        e.preventDefault();
        e.stopPropagation();

        if (window.confirm('Opravdu chcete smazat tento inzerát?')) {
            try {
                await axios.delete(`http://localhost:3001/ad/${ad}/${car}`, { withCredentials: true })
                    .then(res => {
                        if (res.data.Status === 'Success') {
                            toast.success('Inzerát byl úspěšně smazán.');
                            const adElement = document.getElementById(`${ad}`);
                            if (adElement) {
                                adElement.remove();
                            }
                        } else {
                            toast.error(res.data.Error)
                        }
                    });

            } catch (error) {
                toast.error(error)
            }
        }
    };

    const toggleEditPanel = (e) => {
        e.preventDefault();
        e.stopPropagation();

        setIsEditOpen(true);
    };

    const handleSubmit = (e) => {

    }



    useEffect(() => {
        console.log(car.obrazky);
        axios.get('http://localhost:3001/profile', { withCredentials: true })
            .then(res => {
                if (res.data.Status === "Success") {
                    jePrihlasen(true);
                } else {
                    jePrihlasen(false);
                }
            })
    }, [])


    const handleHeartClick = async (e) => {
        e.preventDefault();
        e.stopPropagation();

        if (!auth) {
            toast.error("Musíte se nejdříve přihlásit.");
            return;
        }

        try {
            if (!isFavourite) {
                await axios.post('http://localhost:3001/favor', { carId: car.id, cena: car.cena }, { withCredentials: true });
                toast.success(`${car.nazev} byl přidán do oblíbených.`);
            } else {
                await axios.delete(`http://localhost:3001/favor/${car.id}`, { withCredentials: true });
                toast.success(`${car.nazev} byl odebrán z oblíbených.`);
            }
            setIsFavourite(!isFavourite);
        } catch (err) {
            console.error("Error updating favourites:", err);
        }
    };
    return (
        <Link to={`/car/${car.id}`} style={{ textDecoration: 'none', color: 'inherit' }}>
            <div className='row mb-5 align-items-center'>
                <div className='col-12 col-xl-3 col-lg-3 col-sm-12'>
                    <img alt='auto' className='img-fluid' src={car.obrazky[0]} />
                </div>
                <div className="col-7 col-xl-3 col-lg-3 col-sm-7 d-flex justify-content-center">
                    <div className="card" style={{ border: 'none', width: '100%' }}>
                        <div className="card-body" style={{ textAlign: 'left' }}>
                            <h5 className="card-title fw-bold mb-3">{car.nazev}</h5>
                            <h6 className="card-subtitle fw-semibold mb-3">{car.rok_vyroby}, {Number(car.najete_km).toLocaleString('cs-CZ')} Km</h6>
                            <h6 className="card-subtitle mb-3">{car.vykon_kw} kW ({Math.round(car.vykon_kw * 1.341)} ps)</h6>
                            <h6 className="card-subtitle mb-3">{car.karoserie}, {car.palivo}, {car.prevodovka}</h6>
                            <h6 className="card-subtitle mb-3">Lokace: {car.kraj} kraj</h6>
                        </div>
                    </div>
                </div>
                <div className='col-5 col-xl-2 col-lg-2 col-sm-5 d-flex justify-content-center'>
                    <div className="card" style={{ border: 'none', width: '100%' }}>
                        <div className="card-body" style={{ textAlign: 'right' }}>
                            <h4 className="card-title fw-bold" style={{ color: car.stav === 'Aktivní' ? 'green' : car.stav === 'Rezervovaný' ? 'orange' : car.stav === 'Zrušený' ? 'red' : 'black' }}>{car.stav}</h4>
                            <h5 className="card-subtitle fw-semibold" style={{ marginBottom: 70 }}>{Number(car.cena).toLocaleString('cs-CZ')} Kč</h5>
                            <svg xmlns="http://www.w3.org/2000/svg" width="40" height="40" fillRule="currentColor" className="bi bi-heart" viewBox="0 0 16 16" fill={isFavourite ? "red" : "currentColor"} onClick={(e) => handleHeartClick(e, car.id)} style={{ cursor: 'pointer' }}>
                                <path d="m8 2.748-.717-.737C5.6.281 2.514.878 1.4 3.053c-.523 1.023-.641 2.5.314 4.385.92 1.815 2.834 3.989 6.286 6.357 3.452-2.368 5.365-4.542 6.286-6.357.955-1.886.838-3.362.314-4.385C13.486.878 10.4.28 8.717 2.01zM8 15C-7.333 4.868 3.279-3.04 7.824 1.143q.09.083.176.171a3 3 0 0 1 .176-.17C12.72-3.042 23.333 4.867 8 15" />
                            </svg>
                        </div>
                    </div>
                </div>
                <div className='col-12 col-xl-4 col-lg-4 col-sm-12 d-flex flex-column'>
                    <button style={{ marginBottom: '50px' }} onClick={toggleEditPanel} className="btn-border-radius-lg btn btn-outline-secondary">Upravit</button>
                    <button className="btn btn-danger" onClick={(e) => deleteAd(e, car.id, car.carId)}>Smazat</button>
                </div>
            </div>

            {/* Pokud je panel pro úpravy otevřený, zobrazí se tento div */}
            {isEditOpen && (
                <div className="edit-panel container" onClick={(e) => e.preventDefault()}>
                    <div>
                        <form onSubmit={handleSubmit}>
                            <div className='row' style={{ marginBottom: '50px' }}>
                                <div className='text-center'>
                                    <h2>Inzerát</h2>
                                    <hr></hr>
                                </div>
                                <div className='col-12 col-xxl-6 col-xl-6 col-lg-6 col-md-12 col-sm-12'>
                                    <label htmlFor='nazev' className='form-label'>Název</label>
                                    <input name='nazev' style={{ marginBottom: '20px' }} id='nazev' type='text' className='form-control' placeholder='Audi RS3 MATRIX'
                                        onChange={handleChange}
                                        value={isEditOpen ? editData.car.nazev || "" : data.car.nazev || ""}></input>
                                    <div className='row'>
                                        <div className='col-12 col-xxl-6 col-xl-6 col-lg-6 col-md-6 col-sm-6'>
                                            <label htmlFor='cena' className='form-label'>Cena</label>
                                            <div className="input-group" style={{ marginBottom: '20px' }}>
                                                <input name='cena' onChange={handleChange} value={isEditOpen ? editData.car.cena || "" : data.car.cena || ""} id='cena' type='text' className='form-control' placeholder='1 999 000'></input>
                                                <span className="input-group-text" style={{ backgroundColor: 'white' }}>Kč</span>
                                            </div>
                                        </div>
                                        <div className='col-12 col-xxl-6 col-xl-6 col-lg-6 col-md-6 col-sm-6'>
                                            <label htmlFor='stav' className='form-label'>Stav</label>
                                            <div className="input-group" style={{ marginBottom: '20px' }}>
                                                <select name='stav' onChange={handleChange} id='stav' type='text' className='form-select' value={isEditOpen ? editData.car.stav || "" : data.car.stav || ""}>
                                                    <option value='' disabled>Vyberte stav</option>
                                                    <option value="Aktivní">Aktivní</option>
                                                    <option value="Rezervovaný">Rezervovaný</option>
                                                    <option value="Zrušený">Zrušený</option>
                                                </select>
                                            </div>
                                        </div>
                                    </div>
                                </div>
                                <div className='col-12 col-xxl-6 col-xl-6 col-lg-6 col-md-12 col-sm-12'>
                                    <label htmlFor='popis' className='form-label'>Popis</label>
                                    <textarea name='popis' onChange={handleChange} style={{ marginBottom: '20px', height: '128px' }} id='popis' type='text' className="form-control" placeholder='Zde napiště svůj popis vozu, závady, co chcete přidat k vozu, výbavu atd.'
                                        value={isEditOpen ? editData.car.popis || "" : data.car.popis || ""}></textarea>
                                </div>
                            </div>
                            <div className='row' style={{ marginBottom: '50px' }}>
                                <div className='text-center'>
                                    <h2>Auto</h2>
                                    <hr></hr>
                                </div>
                                <h4>Výrobce</h4>
                                <div className='col-12 col-xxl-6 col-xl-6 col-lg-6 col-md-12 col-sm-12'>
                                    <label htmlFor='znacka' className='form-label'>Značka</label>
                                    <input name='znacka' onChange={handleChange} style={{ marginBottom: '20px' }} id='znacka' type='text' className='form-control' placeholder='Audi' value={isEditOpen ? editData.car.znacka || "" : data.car.znacka || ""}></input>
                                </div>
                                <div className='col-12 col-xxl-6 col-xl-6 col-lg-6 col-md-12 col-sm-12'>
                                    <label htmlFor='model' className='form-label'>Model</label>
                                    <input name='model' onChange={handleChange} style={{ marginBottom: '20px' }} id='model' type='text' className='form-control' placeholder='RS3' value={isEditOpen ? editData.car.model || "" : data.car.model || ""}></input>
                                </div>
                            </div>
                            <div className='row' style={{ marginBottom: '50px' }}>
                                <h4>Typ Vozidla</h4>
                                <div className='col-12 col-xxl-4 col-xl-4 col-lg-4 col-md-12 col-sm-12'>
                                    <label htmlFor='karoserie' className='form-label'>Karoserie</label>
                                    <input name='karoserie' onChange={handleChange} style={{ marginBottom: '20px' }} id='karoserie' type='text' className='form-control' placeholder='Sportovní hatchback' value={isEditOpen ? editData.car.karoserie || "" : data.car.karoserie || ""}></input>
                                </div>
                                <div className='col-12 col-xxl-4 col-xl-4 col-lg-4 col-md-12 col-sm-12'>
                                    <label htmlFor='pocetDveri' className='form-label'>Počet Dveří</label>
                                    <input name='pocet_dveri' onChange={handleChange} style={{ marginBottom: '20px' }} id='pocetDVeri' type='text' className='form-control' placeholder='3' value={isEditOpen ? editData.car.pocet_dveri || "" : data.car.pocet_dveri || ""}></input>
                                </div>
                                <div className='col-12 col-xxl-4 col-xl-4 col-lg-4 col-md-12 col-sm-12'>
                                    <label htmlFor='pocetSedadel' className='form-label'>Počet Sedadel</label>
                                    <input name='pocet_sedadel' onChange={handleChange} style={{ marginBottom: '20px' }} id='pocetSedadel' type='text' className='form-control' placeholder='5' value={isEditOpen ? editData.car.pocet_sedadel || "" : data.car.pocet_sedadel || ""}></input>
                                </div>
                            </div>
                            <div className='row' style={{ marginBottom: '50px' }}>
                                <h4>Specifikace</h4>
                                <div className='col-12 col-xxl-3 col-xl-3 col-lg-3 col-md-12 col-sm-12'>
                                    <label htmlFor='najeto' className='form-label'>Najeto</label>
                                    <div className="input-group" style={{ marginBottom: '20px' }}>
                                        <input name='najete_km' onChange={handleChange} id='najeto' type='text' className='form-control' placeholder='125 000' value={isEditOpen ? editData.car.najete_km || "" : data.car.najete_km || ""}></input>
                                        <span className="input-group-text" style={{ backgroundColor: 'white' }}>Km</span>
                                    </div>
                                </div>
                                <div className='col-12 col-xxl-3 col-xl-3 col-lg-3 col-md-12 col-sm-12'>
                                    <label htmlFor='barva' className='form-label'>Barva</label>
                                    <input name='barva' onChange={handleChange} style={{ marginBottom: '20px' }} id='barva' type='text' className='form-control' placeholder='Černá' value={isEditOpen ? editData.car.barva || "" : data.car.barva || ""}></input>
                                </div>
                                <div className='col-12 col-xxl-3 col-xl-3 col-lg-3 col-md-12 col-sm-12'>
                                    <label htmlFor='rokVyroby' className='form-label'>Rok Výroby</label>
                                    <input name='rok_vyroby' onChange={handleChange} style={{ marginBottom: '20px' }} id='rokVyroby' type='text' className='form-control' placeholder='2022' value={isEditOpen ? editData.car.rok_vyroby || "" : data.car.rok_vyroby || ""}></input>
                                </div>
                                <div className='col-12 col-xxl-3 col-xl-3 col-lg-3 col-md-12 col-sm-12'>
                                    <label htmlFor='prevodovka' className='form-label'>Převodovka</label>
                                    <div className="input-group" style={{ marginBottom: '20px' }}>
                                        <select name='prevodovka' onChange={handleChange} id='prevodovka' type='text' className='form-select' value={isEditOpen ? editData.car.prevodovka || "" : data.car.prevodovka || ""}>
                                            <option value='' disabled>Vyberte Převodovku</option>
                                            <option value="Automatická">Automatická</option>
                                            <option value="Manuální">Manuální</option>
                                            <option value="Polo-automatická">Polo-automatická</option>
                                        </select>
                                    </div>
                                </div>
                                <div className='col-12 col-xxl-3 col-xl-3 col-lg-3 col-md-12 col-sm-12'>
                                    <label htmlFor='vykon' className='form-label'>Výkon</label>
                                    <div className="input-group" style={{ marginBottom: '20px' }}>
                                        <input name='vykon_kw' onChange={handleChange} id='vykon' type='text' className='form-control' placeholder='125' value={isEditOpen ? editData.car.vykon_kw || "" : data.car.vykon_kw || ""}></input>
                                        <span className="input-group-text" style={{ backgroundColor: 'white' }}>kW</span>
                                    </div>
                                </div>
                                <div className='col-12 col-xxl-3 col-xl-3 col-lg-3 col-md-12 col-sm-12'>
                                    <label htmlFor='objem' className='form-label'>Objem</label>
                                    <div className="input-group" style={{ marginBottom: '20px' }}>
                                        <input name='objem' onChange={handleChange} id='objem' type='text' className='form-control' placeholder='2 480' value={isEditOpen ? editData.car.objem || "" : data.car.objem || ""}></input>
                                        <span className="input-group-text" style={{ backgroundColor: 'white' }}>ccm</span>
                                    </div>
                                </div>
                                <div className='col-12 col-xxl-3 col-xl-3 col-lg-3 col-md-12 col-sm-12'>
                                    <label htmlFor='palivo' className='form-label'>Palivo</label>
                                    <div className="input-group" style={{ marginBottom: '20px' }}>
                                        <select name='palivo' onChange={handleChange} id='palivo' type='text' className='form-select' value={isEditOpen ? editData.car.palivo || "" : data.car.palivo || ""}>
                                            <option value='' disabled>Vyberte Palivo</option>
                                            <option value="Benzín">Benzín</option>
                                            <option value="Nafta">Nafta</option>
                                            <option value="Elektro">Elektro</option>
                                            <option value="Hybrid">Hybrid</option>
                                        </select>
                                    </div>
                                </div>
                                <div className='col-12 col-xxl-3 col-xl-3 col-lg-3 col-md-12 col-sm-12'>
                                    <label htmlFor='pohon' className='form-label'>Pohon</label>
                                    <div className="input-group" style={{ marginBottom: '20px' }}>
                                        <select name='pohon' onChange={handleChange} id='pohon' type='text' className='form-select' value={isEditOpen ? editData.car.pohon || "" : data.car.pohon || ""}>
                                            <option value='' disabled>Vyberte Pohon</option>
                                            <option value="Přední pohon">Přední pohon</option>
                                            <option value="Zadní pohon">Zadní pohon</option>
                                            <option value="Pohon všech kol">Pohon všech kol</option>
                                        </select>
                                    </div>
                                </div>
                            </div>
                            <div className='row' style={{ marginBottom: '50px' }}>
                                <h4>VIN</h4>
                                <div className='col-12'>
                                    <label htmlFor='vin' className='form-label'>Kód není povinný, je to ve vašem zájmu</label>
                                    <input name='vin' onChange={handleChange} id='vin' type='text' className='form-control' placeholder='TMBAEA200P0635724' value={isEditOpen ? editData.car.vin || "" : data.car.vin || ""}></input>
                                </div>
                            </div>
                            <div className='row' style={{ marginBottom: '50px' }}>
                                <div className='text-center'>
                                    <h2>Fotogalerie</h2>
                                    <hr></hr>
                                </div>
                                <div className='col-12 text-center'>
                                    <div className="col-12 text-center">
                                        <div
                                            className="drag-drop-area"
                                            onDrop={handleDrop}
                                            onDragOver={handleDragOver}
                                            style={{
                                                border: '2px dashed #ccc',
                                                padding: '20px',
                                                borderRadius: '5px',
                                                cursor: 'pointer',
                                                backgroundColor: '#f9f9f9',
                                            }}
                                        >
                                            <p>Přetáhněte sem obrázky.</p>
                                            <input
                                                type="file"
                                                multiple
                                                onChange={handleFileInputChange}
                                                style={{ display: 'none' }}
                                                id="fileInput"
                                            />
                                            <label htmlFor="fileInput" style={{ cursor: 'pointer', textDecoration: 'underline' }}>
                                                Klikněte pro zobrazení průzkumníka
                                            </label>
                                            {droppedFiles.length > 0 && (
                                                <div>
                                                    <h5>Nahrané obrázky</h5>
                                                    <ul style={{ listStyleType: 'none', padding: 0 }}>
                                                        {droppedFiles.map((file, index) => (
                                                            <li key={index} style={{ margin: '10px 0' }}>
                                                                {file.type.startsWith('image/') ? (
                                                                    <div style={{ alignItems: 'center' }}>
                                                                        <img
                                                                            src={URL.createObjectURL(file)}
                                                                            alt={file.name}
                                                                            width="100"
                                                                            style={{ marginRight: '10px' }}
                                                                        />
                                                                        <span>{file.name}</span>
                                                                        <button
                                                                            onClick={(event) => handleDelete(event, file)}
                                                                            style={{
                                                                                marginLeft: '10px',
                                                                                background: 'red',
                                                                                color: 'white',
                                                                                border: 'none',
                                                                                padding: '5px 10px',
                                                                                cursor: 'pointer',
                                                                                borderRadius: '5px',
                                                                            }}
                                                                        >
                                                                            Smazat
                                                                        </button>
                                                                    </div>
                                                                ) : (
                                                                    <div style={{ display: 'flex', alignItems: 'center' }}>
                                                                        <span>{file.name}</span>
                                                                        <button
                                                                            onClick={(event) => handleDelete(event, file)}
                                                                            style={{
                                                                                marginLeft: '10px',
                                                                                background: 'red',
                                                                                color: 'white',
                                                                                border: 'none',
                                                                                padding: '5px 10px',
                                                                                cursor: 'pointer',
                                                                                borderRadius: '5px',
                                                                            }}
                                                                        >
                                                                            Smazat
                                                                        </button>
                                                                    </div>
                                                                )}
                                                            </li>
                                                        ))}
                                                    </ul>
                                                </div>
                                            )}
                                        </div>
                                    </div>
                                </div>
                            </div>
                            <div className='row justify-content-center'>
                                <div className='mb-3 col-12 form-group'>
                                    <button className='btn-border-radius-lg btn btn-outline-secondary' style={{ marginBottom: '10px', width: '100%' }} type="submit">Uložit změny</button>
                                    <button className='btn-border-radius-lg btn btn-outline-secondary' style={{ marginBottom: '10px', width: '100%' }} onClick={(e) => toggleEditPanel(e)}>Zavřít</button>
                                </div>
                            </div>
                        </form>
                    </div>
                </div>
            )}
        </Link>
    );
};
export default CarAddFav;
