import React, { useState } from 'react';

function DragAndDrop() {
  const [droppedFiles, setDroppedFiles] = useState([]);

  const handleDrop = (event) => {
    event.preventDefault();
    const files = Array.from(event.dataTransfer.files);
    setDroppedFiles((prevFiles) => [...prevFiles, ...files]);
    console.log('Dropped files:', files);
  };

  const handleDragOver = (event) => {
    event.preventDefault();
  };

  const handleFileInputChange = (event) => {
    const files = Array.from(event.target.files);
    setDroppedFiles((prevFiles) => [...prevFiles, ...files]);
  };

  return (
    <div className="col-12 text-center">
      <div
        className="drag-drop-area"
        onDrop={handleDrop}
        onDragOver={handleDragOver}
        style={{
          border: '2px dashed #ccc',
          padding: '20px',
          borderRadius: '5px',
          cursor: 'pointer',
          backgroundColor: '#f9f9f9',
        }}
      >
        <p>Drag and drop multiple files here, or click to select files.</p>
        <input
          type="file"
          multiple
          onChange={handleFileInputChange}
          style={{ display: 'none' }}
          id="fileInput"
        />
        <label htmlFor="fileInput" style={{ cursor: 'pointer', textDecoration: 'underline' }}>
          Click to select files
        </label>
        {droppedFiles.length > 0 && (
          <div>
            <h5>Files ready for upload:</h5>
            <ul style={{ listStyleType: 'none', padding: 0 }}>
              {droppedFiles.map((file, index) => (
                <li key={index} style={{ margin: '10px 0' }}>
                  {file.type.startsWith('image/') ? (
                    <div>
                      <img
                        src={URL.createObjectURL(file)}
                        alt={file.name}
                        width="100"
                        style={{ marginRight: '10px' }}
                      />
                      <span>{file.name}</span>
                    </div>
                  ) : (
                    <span>{file.name}</span>
                  )}
                </li>
              ))}
            </ul>
          </div>
        )}
      </div>
    </div>
  );
}

export default DragAndDrop;
