import React from 'react';
import axios from 'axios';
import toast from 'react-hot-toast';
import { Link } from 'react-router-dom';

export default function Ad({ ad }) {

  const handleDelete = async (adId, e) => {
    e.preventDefault();
    try {
      const response = await axios.delete(`http://localhost:3001/ad/${adId}`, { withCredentials: true });
      if (response.data.Status === 'Success') {
        toast.success('Inzerát úspěšně smazán.');
      } else {
        toast.error(response.data.Error);
      }
    } catch (error) {
      toast.error('Chyba při mazání inzerátu.');
    }
  };

  return (
    <Link to={`/car/${ad.id}`} style={{ textDecoration: 'none', color: 'inherit' }}>
      <div className="card mb-3">
        <div className="card-body">
          <h5 className="card-title">{ad.nazev}</h5>
          <p className="card-text">Cena: {ad.cena} Kč</p>
          <p className="card-text">Inzerent: {ad.jmeno} {ad.prijmeni}</p>
          <p className="card-text">Email: {ad.email}</p>
          <div className="d-flex justify-content-between">
            <button className="btn btn-danger" onClick={(e) => handleDelete(ad.id, e)}>
              Smazat
            </button>
          </div>
        </div>
      </div>
    </Link>
  );
}
