                                    <label htmlFor='vin' className='form-label'>Kód není povinný, je to ve vašem zájmu</label>
                                    <input id='vin' type='text' className='form-control' placeholder='TMBAEA200P0635724'
                                        onChange={e => setSpecifikace({ ...specifikace, vin: e.target.value })}></input>
                                </div>
                            </div>
                            <div className='row' style={{ marginBottom: '50px' }}>
                                <div className='text-center'>
                                    <h2>Fotogalerie</h2>
                                    <hr></hr>
                                </div>
                                <div className='col-12 text-center'>
                                    <div className="col-12 text-center">
                                        <div
                                            className="drag-drop-area"
                                            onDrop={handleDrop}
                                            onDragOver={handleDragOver}
                                            style={{
                                                border: '2px dashed #ccc',
                                                padding: '20px',
                                                borderRadius: '5px',
                                                cursor: 'pointer',
                                                backgroundColor: '#f9f9f9',
                                            }}
                                        >
                                            <p>Přetáhněte sem obrázky.</p>
                                            <input
                                                type="file"
                                                multiple
                                                onChange={handleFileInputChange}
                                                style={{ display: 'none' }}
                                                id="fileInput"
                                            />
                                            <label htmlFor="fileInput" style={{ cursor: 'pointer', textDecoration: 'underline' }}>
                                                Klikněte pro zobrazení průzkumníka
                                            </label>
                                            {droppedFiles.length > 0 && (
                                                <div>
                                                    <h5>Nahrané obrázky</h5>
                                                    <ul style={{ listStyleType: 'none', padding: 0 }}>
                                                        {droppedFiles.map((file, index) => (
                                                            <li key={index} style={{ margin: '10px 0' }}>
                                                                {file.type.startsWith('image/') ? (
                                                                    <div style={{ alignItems: 'center' }}>
                                                                        <img
                                                                            src={URL.createObjectURL(file)}
                                                                            alt={file.name}
                                                                            width="100"
                                                                            style={{ marginRight: '10px' }}
                                                                        />
                                                                        <span>{file.name}</span>
                                                                        <button
                                                                            onClick={(event) => handleDelete(event, file)}
                                                                            style={{
                                                                                marginLeft: '10px',
                                                                                background: 'red',
                                                                                color: 'white',
                                                                                border: 'none',
                                                                                padding: '5px 10px',
                                                                                cursor: 'pointer',
                                                                                borderRadius: '5px',
                                                                            }}
                                                                        >
                                                                            Smazat
                                                                        </button>
                                                                    </div>
                                                                ) : (
                                                                    <div style={{ display: 'flex', alignItems: 'center' }}>
                                                                        <span>{file.name}</span>
                                                                        <button
                                                                            onClick={(event) => handleDelete(event, file)}
                                                                            style={{
                                                                                marginLeft: '10px',
                                                                                background: 'red',
                                                                                color: 'white',
                                                                                border: 'none',
                                                                                padding: '5px 10px',
                                                                                cursor: 'pointer',
                                                                                borderRadius: '5px',
                                                                            }}
                                                                        >
                                                                            Smazat
                                                                        </button>
                                                                    </div>
                                                                )}
                                                            </li>
                                                        ))}
                                                    </ul>
                                                </div>
                                            )}
                                        </div>
                                    </div>
                                </div>
                            </div>
                            <div className='row justify-content-center'>
                                <div className='mb-3 col-12 form-group'>
                                    <button className='btn-border-radius-lg btn btn-outline-secondary' style={{ marginBottom: '10px', width: '50%' }} type="submit">Uložit změny</button>
                                    <button className='btn-border-radius-lg btn btn-outline-secondary' style={{ marginBottom: '10px', width: '50%' }} onClick={(e) => toggleEditPanel(e)}>Zavřít</button>
                                </div>
                            </div>
                        </form>
                        */}
                    </div>
                </div>
            )}
        </Link>
    );
