import React, { useEffect, useState } from 'react';
import toast from 'react-hot-toast';

function DragAndDrop() {
  const [droppedFiles, setDroppedFiles] = useState([]);

  const handleDrop = (event) => {
    event.preventDefault(); // Zabránění odeslání formuláře při přetahování souborů
    const files = Array.from(event.dataTransfer.files);

    const validFiles = files.filter(file => file.type.startsWith('image/'));
    const invalidFiles = files.filter(file => !file.type.startsWith('image/'));

    if (invalidFiles.length > 0) {
        toast.error(`Některé soubory nejsou obrázky: ${invalidFiles.map(f => f.name).join(', ')}`);
      }

    setDroppedFiles((prevFiles) => [...prevFiles, ...validFiles]);
  };

  const handleDragOver = (event) => {
    event.preventDefault(); // Zabránění odeslání formuláře při dragOver
  };

  const handleFileInputChange = (event) => {
    const files = Array.from(event.target.files);
    setDroppedFiles((prevFiles) => [...prevFiles, ...files]);
  };

  const handleDelete = (event, fileToDelete) => {
    event.preventDefault(); // Zabránění odeslání formuláře při smazání obrázku
    setDroppedFiles((prevFiles) =>
      prevFiles.filter((file) => file !== fileToDelete)
    );
  };

  useEffect(() => {
    console.log('Dropped files:', droppedFiles);
  }, [droppedFiles])

  return (
    <div className="col-12 text-center">
      <div
        className="drag-drop-area"
        onDrop={handleDrop}
        onDragOver={handleDragOver}
        style={{
          border: '2px dashed #ccc',
          padding: '20px',
          borderRadius: '5px',
          cursor: 'pointer',
          backgroundColor: '#f9f9f9',
        }}
      >
        <p>Přetáhněte sem obrázky.</p>
        <input
          type="file"
          multiple
          onChange={handleFileInputChange}
          style={{ display: 'none' }}
          id="fileInput"
        />
        <label htmlFor="fileInput" style={{ cursor: 'pointer', textDecoration: 'underline' }}>
          Klikněte pro zobrazení průzkumníka
        </label>
        {droppedFiles.length > 0 && (
          <div>
            <h5>Nahrané obrázky</h5>
            <ul style={{ listStyleType: 'none', padding: 0 }}>
              {droppedFiles.map((file, index) => (
                <li key={index} style={{ margin: '10px 0' }}>
                  {file.type.startsWith('image/') ? (
                    <div style={{ alignItems: 'center' }}>
                      <img
                        src={URL.createObjectURL(file)}
                        alt={file.name}
                        width="100"
                        style={{ marginRight: '10px' }}
                      />
                      <span>{file.name}</span>
                      <button
                        onClick={(event) => handleDelete(event, file)}
                        style={{
                          marginLeft: '10px',
                          background: 'red',
                          color: 'white',
                          border: 'none',
                          padding: '5px 10px',
                          cursor: 'pointer',
                          borderRadius: '5px',
                        }}
                      >
                        Smazat
                      </button>
                    </div>
                  ) : (
                    <div style={{ display: 'flex', alignItems: 'center' }}>
                      <span>{file.name}</span>
                      <button
                        onClick={(event) => handleDelete(event, file)}
                        style={{
                          marginLeft: '10px',
                          background: 'red',
                          color: 'white',
                          border: 'none',
                          padding: '5px 10px',
                          cursor: 'pointer',
                          borderRadius: '5px',
                        }}
                      >
                        Smazat
                      </button>
                    </div>
                  )}
                </li>
              ))}
            </ul>
          </div>
        )}
      </div>
    </div>
  );
}

export default DragAndDrop;
