import React, { useState, useEffect } from 'react';
import axios from 'axios';

export default function Search() {
  const [makes, setMakes] = useState([]); // State pro značky
  const [models, setModels] = useState([]); // State pro modely
  const [selectedMake, setSelectedMake] = useState(''); // State pro vybranou značku

  // Funkce pro získání značek
  const fetchMakes = async () => {
    try {
      const response = await axios.get('http://localhost:3001/api/getMakes');
      setMakes(response.data); // Uložení značek do stavu
    } catch (error) {
      console.error('Chyba při získávání značek:', error);
    }
  };

  // Funkce pro získání modelů pro určitou značku
  const fetchModels = async (make) => {
    try {
      const response = await axios.get(`http://localhost:3001/api/getModels/${make}`);
      setModels(response.data); // Uložení modelů do stavu
    } catch (error) {
      console.error(`Chyba při získávání modelů pro značku ${make}:`, error);
    }
  };

  // Volání funkce fetchMakes při prvním renderování komponenty
  useEffect(() => {
    fetchMakes();
  }, []); // Prázdné pole znamená, že se volá pouze při prvním načtení komponenty

  // Volání funkce fetchModels, když se změní vybraná značka
  useEffect(() => {
    if (selectedMake) {
      fetchModels(selectedMake); // Zavolání fetchModels, když je vybrána značka
    }
  }, [selectedMake]); // Tato funkce se zavolá vždy, když se změní selectedMake

  return (
    <div>
      <h1>Vyberte auto</h1>
      
      {/* Výběr značky */}
      <select onChange={(e) => setSelectedMake(e.target.value)} value={selectedMake}>
        <option value="">Vyberte značku</option>
        {makes.map((make) => (
          <option key={make.make} value={make.make}>
            {make.make}
          </option>
        ))}
      </select>

      {/* Výběr modelu */}
      <select>
        <option value="">Vyberte model</option>
        {models.map((model) => (
          <option key={model.model} value={model.model}>
            {model.model}
          </option>
        ))}
      </select>
    </div>
  );
}
