import React from 'react'
import axios from 'axios';
import { useParams } from 'react-router-dom';
import { useEffect } from 'react';
import { useState } from 'react';
import toast from 'react-hot-toast';

export default function SingleCar() {
  const { id } = useParams();
  const [carData, setCarData] = useState(null);

  const handleGoBack = () => {
    navigate(-1);
  };

  useEffect(() => {
    axios.post(`http://localhost:3001/car/${id}`)
      .then(res => {
        if (res.data.Status === "Success") {
          setCarData(res.data);
        } else {
          toast.error(res.data.Error)
        }
      })
      .catch(error => {
        toast.error("Došlo k chybě při náčítání stránky profilu. " + error);
      });
  }, [id]);
  return (
    <>
      <div className='container'>
        <div className='row'>
          <div className='col col-xxl-12 col-xl-12 col-lg-12 col-md-12 col-sm-12'>
            
            <h6 onClick={handleGoBack} style={{ cursor: 'pointer' }}>&lt; Zpět na výpis</h6>
          </div>
        </div>

      </div>
    </>
  )
}
