import React, { useState } from 'react';

function DragAndDrop() {
  const [droppedFiles, setDroppedFiles] = useState([]);

  const handleDrop = (event) => {
    event.preventDefault(); // Zabráníme výchozímu chování odesílání formuláře
    const files = Array.from(event.dataTransfer.files);
    setDroppedFiles((prevFiles) => [...prevFiles, ...files]);
    console.log('Dropped files:', files);
  };

  const handleDragOver = (event) => {
    event.preventDefault(); // Zabráníme výchozímu chování odesílání formuláře
  };

  const handleFileInputChange = (event) => {
    const files = Array.from(event.target.files);
    setDroppedFiles((prevFiles) => [...prevFiles, ...files]);
  };

  const handleDelete = (fileToDelete) => {
    setDroppedFiles((prevFiles) =>
      prevFiles.filter((file) => file !== fileToDelete)
    );
  };

  const handleFormSubmit = (event) => {
    event.preventDefault(); // Zabráníme odeslání formuláře
    console.log('Formular odeslán, ale nic se nestane');
  };

  return (
    <form onSubmit={handleFormSubmit}>
      <div className="col-12 text-center">
        <div
          className="drag-drop-area"
          onDrop={handleDrop}
          onDragOver={handleDragOver}
          style={{
            border: '2px dashed #ccc',
            padding: '20px',
            borderRadius: '5px',
            cursor: 'pointer',
            backgroundColor: '#f9f9f9',
          }}
        >
          <p>Přetáhněte sem obrázky.</p>
          <input
            type="file"
            multiple
            onChange={handleFileInputChange}
            style={{ display: 'none' }}
            id="fileInput"
          />
          <label htmlFor="fileInput" style={{ cursor: 'pointer', textDecoration: 'underline' }}>
            Klikněte pro zobrazení průzkumníka
          </label>
          {droppedFiles.length > 0 && (
            <div>
              <h5>Nahrané obrázky</h5>
              <ul style={{ listStyleType: 'none', padding: 0 }}>
                {droppedFiles.map((file, index) => (
                  <li key={index} style={{ margin: '10px 0' }}>
                    {file.type.startsWith('image/') ? (
                      <div style={{ display: 'flex', alignItems: 'center' }}>
                        <img
                          src={URL.createObjectURL(file)}
                          alt={file.name}
                          width="100"
                          style={{ marginRight: '10px' }}
                        />
                        <span>{file.name}</span>
                        <button
                          onClick={() => handleDelete(file)}
                          style={{
                            marginLeft: '10px',
                            background: 'red',
                            color: 'white',
                            border: 'none',
                            padding: '5px 10px',
                            cursor: 'pointer',
                            borderRadius: '5px',
                          }}
                        >
                          Smazat
                        </button>
                      </div>
                    ) : (
                      <div style={{ display: 'flex', alignItems: 'center' }}>
                        <span>{file.name}</span>
                        <button
                          onClick={() => handleDelete(file)}
                          style={{
                            marginLeft: '10px',
                            background: 'red',
                            color: 'white',
                            border: 'none',
                            padding: '5px 10px',
                            cursor: 'pointer',
                            borderRadius: '5px',
                          }}
                        >
                          Smazat
                        </button>
                      </div>
                    )}
                  </li>
                ))}
              </ul>
            </div>
          )}
        </div>
      </div>
      <button type="submit" style={{ marginTop: '20px' }}>Odeslat formulář</button>
    </form>
  );
}

export default DragAndDrop;
