import { Link } from "react-router-dom";
import { useState } from "react";

const CarAdd = ({ car }) => {
  const [isFavorite, setIsFavorite] = useState(false);

  const handleHeartClick = (e) => {
    e.stopPropagation(); // Zastaví propagaci události na rodiče
    setIsFavorite((prev) => !prev); // Přepíná stav oblíbenosti
    console.log(`${car.nazev} ${!isFavorite ? 'přidáno do' : 'odebráno z'} oblíbených.`);
};
    return (
      <Link to={`/car/${car.id}`} style={{ textDecoration: 'none', color: 'inherit' }}>
      <div className='row mb-5 align-items-center'>
        <div className='col-12 col-xl-4 col-lg-4 col-sm-12'>
          <img alt='auto' className='img-fluid' src={'https://via.placeholder.com/150'} />
        </div>
        <div className="col-7 col-xl-5 col-lg-5 col-sm-8 d-flex justify-content-center">
          <div className="card" style={{ border: 'none', width: '100%' }}>
            <div className="card-body" style={{ textAlign: 'left' }}>
              <h4 className="card-title fw-bold mb-3">{car.nazev}</h4>
              <h6 className="card-subtitle fw-semibold mb-3">{car.rok_vyroby}, {car.najete_km} Km</h6>
              <h6 className="card-subtitle mb-3">{car.vykon_kw} kW</h6>
              <h6 className="card-subtitle mb-3">{car.karoserie}, {car.palivo}, {car.prevodovka}</h6>
              <h6 className="card-subtitle mb-3">Lokace: {car.kraj} kraj</h6>
            </div>
          </div>
        </div>
        <div className='col-5 col-xl-3 col-lg-3 col-sm-4 d-flex justify-content-center'>
          <div className="card" style={{ border: 'none', width: '100%' }}>
            <div className="card-body" style={{ textAlign: 'right' }}>
              <h4 className="card-title fw-bold">{car.stav}</h4>
              <h5 className="card-subtitle fw-semibold" style={{ marginBottom: 70 }}>{car.cena} Kč</h5>
              <svg xmlns="http://www.w3.org/2000/svg" width="40" height="40" fillRule="currentColor" className="bi bi-heart" viewBox="0 0 16 16" fill={isFavorite ? "red" : "currentColor"} onClick={handleHeartClick} style={{ cursor: 'pointer' }}>
                <path d="m8 2.748-.717-.737C5.6.281 2.514.878 1.4 3.053c-.523 1.023-.641 2.5.314 4.385.92 1.815 2.834 3.989 6.286 6.357 3.452-2.368 5.365-4.542 6.286-6.357.955-1.886.838-3.362.314-4.385C13.486.878 10.4.28 8.717 2.01zM8 15C-7.333 4.868 3.279-3.04 7.824 1.143q.09.083.176.171a3 3 0 0 1 .176-.17C12.72-3.042 23.333 4.867 8 15" />
              </svg>
            </div>
          </div>
        </div>
      </div>
      </Link>
    );
  };
export default CarAdd;
